class Solution
{
public:
    long long minimumOperations(vector<int> const &nums, vector<int> const &target)
    {
        int const n = nums.size();
        vector<int> diffs(n + 2);
        for (int i = 0; i != n; ++i) diffs[i + 1] = target[i] - nums[i];
        long long operation_count = 0;
        for (int i = 1; i != n + 2; ++i)
            if (diffs[i] > diffs[i - 1]) operation_count += diffs[i] - diffs[i - 1];
        return operation_count;
    }
};
