class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int in=0,de=0;
        vector<long long> maxi,mini;
        long long ans =0;
        for(int i=0;i<n;i++){
             if(target[i]-nums[i]>0){
                 if(de){
                    ans += mini[0];
                    for(int k=1;k<mini.size();k++){
                         if(mini[k]<mini[k-1])
                         continue ;
                         else{
                            ans += mini[k]-mini[k-1];
                         }
                    }
                     mini.clear();
                    de=0;
         }
                 in=1;
                 maxi.push_back(target[i]-nums[i]);
             }
             else if(target[i]-nums[i]<0){
                if(in){
                  ans +=maxi[0];
                  for(int k=1;k<maxi.size();k++){
                      if(maxi[k-1]>maxi[k])
                      continue ;
                       else
                        ans += maxi[k]-maxi[k-1];
                  }
                      in=0;
                      maxi.clear();
                }
                  de=1;
                  mini.push_back(abs(target[i]-nums[i]));
             }
             else{
                if(de){
                    ans += mini[0];
                    for(int k=1;k<mini.size();k++){
