                         }
                    }
                     mini.clear();
                    de=0;
                 }
                 if(in){
                  ans +=maxi[0];
                  for(int k=1;k<maxi.size();k++){
                      if(maxi[k-1]>maxi[k])
                      continue ;
                       else
                        ans += maxi[k]-maxi[k-1];
                  }
                      in=0;
                      maxi.clear();
                }
             }
        }
          if(de){
                    ans += mini[0];
                    for(int k=1;k<mini.size();k++){
                         if(mini[k]<mini[k-1])
                         continue ;
                         else{
                            ans += mini[k]-mini[k-1];
                         }
                    }
                     mini.clear();
                    de=0;
                 }
        if(in){
                  ans +=maxi[0];
                  for(int k=1;k<maxi.size();k++){
                      if(maxi[k-1]>maxi[k])
                      continue ;
                       else
                        ans += maxi[k]-maxi[k-1];
                  }
                      in=0;
                      maxi.clear();
                }
                return ans ;
    }
