class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> v;
        for(int i = 0; i<n; i++){
            v.push_back(target[i]-nums[i]);
        }
        long long ans = 0, last = 0;
        for(int i = 0; i<n; i++){
            if(last*v[i]<=0){
                ans += 1LL*abs(v[i]); last = v[i];
            }
            else if(last<0){
                if(last<v[i]){
                    last = v[i];
                }
                else{
                    ans += 1LL*abs(last-v[i]);last = v[i];
                }
            }
            else{
                if(last<v[i]){
                    ans += 1LL*abs(v[i]-last); last = v[i];
                }
                else{
                    last = v[i];
                }
            }
            // cout<<i<<"->"<<ans<<endl;
        }
        return ans;
    }
};
