class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [t - n for t, n in zip(target, nums)]
        changes = 0
        mag, is_pos = (abs(diffs[0]), diffs[0] >= 0)
        changes += mag
        for diff in diffs[1:]:
            n_mag = abs(diff)
            n_sign = diff >= 0
            if n_sign == is_pos:
                if n_mag > mag:
                    changes += n_mag - mag
            else:
                changes += n_mag
            mag = n_mag
            is_pos = n_sign
        return changes
