class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        vector<int> aux;
        for(int i=0;i<nums.size();i++){
            aux.push_back(target[i]-nums[i]);
        }
        ans = abs(aux[0]);
        /*for(auto it:aux) cout<<it<<" ";
        cout<<"\n";*/
        for(int i=0;i<aux.size()-1;i++){
            //SIGNO DIFERENTE
            if((aux[i] > 0 && aux[i + 1] < 0) || (aux[i] < 0 && aux[i + 1] > 0)){
                ans+=abs(aux[i+1]);
            }else{
                if(abs(aux[i+1])>abs(aux[i])) ans+=(abs(aux[i+1])-abs(aux[i]));
            }
            //cout<<ans<<" ";
        }
        return ans;
    }
};
