class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long result = 0;
        int left = 0;
        int n = nums.size();

        while (left < n) {
            if (nums[left] == target[left]) {
                left++;
                continue;
            }
            long long tmp = target[left] - nums[left];
            int i = left;
            if (tmp > 0) {
                while (i < n && target[i] - nums[i] > 0) {
                    tmp = min(tmp, (long long)(target[i] - nums[i]));
                    i++;
                }
                for (int j = left; j < i; j++) {
                    nums[j] += tmp;
                }
                result += tmp;
            } else {
                while (i < n && target[i] - nums[i] < 0) {
                    tmp = max(tmp, (long long)(target[i] - nums[i]));
                    i++;
                }
                for (int j = left; j < i; j++) {
                    nums[j] += tmp;
                }
                result -= tmp;
            }
        }

        return result;
    }
};
