class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        long long res=0;

        vector<int> S1, S2;

        for (int i=0; i<nums.size(); ++i) {
            int diff=t[i]-nums[i];
            bool free=false;

            if (diff >=0) {
                S2.clear();
                while(!S1.empty() && S1.back() > diff) {
                    free=true;
                    S1.pop_back();
                }
                if (!free) {
                    if (S1.empty()) res+=diff;
                    else res+=diff-S1.back();
                }
                S1.push_back(diff);
            } else {
                S1.clear();
                while(!S2.empty() && S2.back() < diff) {
                    free=true;
                    S2.pop_back();
                }
                if (!free) {
                    if (S2.empty()) res+=abs(diff);
                    else res+=S2.back()-diff;
                }
                S2.push_back(diff);
            }
        }

        return res;
    }
};
