class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [ni - ti for ni, ti in zip(nums, target)]
        ans = 0
        lst = 0
        for di in diff:
            if lst >= 0 and di > lst:
                ans += di - lst
            elif lst <= 0 and di < lst:
                ans += lst - di
            elif (lst >= 0 and di < 0) or (lst <= 0 and di > 0):
                ans += abs(di)
            lst = di
        return ans
