class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0 , tep = 0 , sum = 0;
        for(int i = 0 ; i < nums.size() ; i++) {
            if(nums[i] > target[i]) {
                tep = 0;
                if(sum < nums[i] - target[i]) {
                    ans += (nums[i] - target[i] - sum);
                }
                sum = nums[i] - target[i];
            } else if(nums[i] < target[i]){
                sum = 0;
                if(tep < target[i] - nums[i]) {
                    ans += (target[i] - nums[i] - tep);
                }
                tep = target[i] - nums[i];
            } else {
                sum = 0;
                tep = 0;
            }
        }
        return ans;
    }
};
