typedef long long LL;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        LL ans=0;
        int n=nums.size();
        vector<int> a(n);
        vector<int> b(n);
        a[0]=nums[0];
        b[0]=target[0];
        for(int i=1;i<n;i++)
        {
            a[i]=nums[i]-nums[i-1];
        }
        for(int i=1;i<n;i++)
        {
            b[i]=target[i]-target[i-1];
        }
        LL pos=0,neg=0;
        for(int i=0;i<n;i++)
        {
            if(a[i]>b[i])
            pos+=(LL)a[i]-b[i];
            else neg+=(LL)b[i]-a[i];
        }
        return max(pos,neg);
    }
};
