class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        delta = [target[i]-nums[i] for i in range(n)]
        delta.append(0)
        # print(delta)
        ans = 0
        for i in range(n):
            if delta[i]*delta[i-1] > 0:
                if abs(delta[i]) <= abs(delta[i-1]):
                    d = 0
                else:
                    d = abs(delta[i]-delta[i-1])
            else:
                d = min(abs(delta[i]),abs(delta[i]-delta[i-1]))
            ans += d  
            # print(ans)
        return ans
