class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        vector<int> v(n);
        for(int i=0;i<n;i++){
            v[i]=target[i]-nums[i];
        }
        int maxv=0,minv=0;
        maxv=abs(v[0]);
        for(int i=0;i<n-1;i++){
            if((v[i]>0&&v[i+1]>0)||(v[i]<0&&v[i+1]<0)){
                if(abs(v[i+1])>maxv)maxv=abs(v[i+1]);
                else{
                    ans+=maxv-abs(v[i+1]);
                    maxv=abs(v[i+1]);
                }
            }
            else{
                ans+=maxv;
                maxv=abs(v[i+1]);
            }
        }
        ans+=maxv;
        return ans;
        //4 1 4 1
        // 0 -3 0 4+3=7
        //  4 3 2 -1 
    }
};
