class Solution {
public:
    long long minNumberOperations(vector<int>& target) {
        long long count=*min_element(target.begin(),target.end());
        int n=target.size();
        vector<int> nextsmall(n,-1);
        vector<int> prevsmall(n,-1);
        stack<int> s;
        s.push(0);
        for(int i=1;i<n;i++){
            while(!s.empty() && target[i]<=target[s.top()]){nextsmall[s.top()]=target[i];s.pop();}
            s.push(i);
        }
        while(!s.empty()){s.pop();}
        s.push(n-1);
        for(int i=n-2;i>-1;i--){
            while(!s.empty() && target[i]<target[s.top()]){prevsmall[s.top()]=target[i];s.pop();}
            s.push(i);
        }
        for(int i=0;i<n;i++){
            int temp=max(prevsmall[i],nextsmall[i]);
            if(temp==-1){continue;}
            count+=target[i]-temp;
        }
        return count;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long result=0;
        for(int i=0;i<nums.size();) {
            vector<int> ns;
            if(target[i]>=nums[i]) {
                while(i<nums.size()&&target[i]>=nums[i])
                    ns.push_back(target[i]-nums[i]),i++;
            } else if(target[i]<nums[i]) {
                while(i<nums.size()&&target[i]<nums[i])
                    ns.push_back(nums[i]-target[i]),i++;
            }
            result+=minNumberOperations(ns);
        }
        return result;
    }
};
