class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        sol = 0
        positive = 0
        negative = 0

        n = len(nums)

        for i in range(n):
            diff = target[i] - nums[i]
            if diff > 0:
                if diff > positive:
                    sol += diff - positive
                positive = diff
                negative = 0

            elif diff < 0:
                if abs(diff) > negative:
                    sol += abs(diff) - negative
                negative = abs(diff) 
                positive = 0    

            else:
                positive, negative = 0, 0

        return sol
