using ll = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        ll n = nums.size(), ans = 0;
        vector<ll> dif(n);
        for(int i = 0; i < n; i++)dif[i] = t[i] - nums[i];
        for(int i = 0; i < n; i++){
            if(dif[i] >= 0){
                ll j = i, prev = 0;
                while(j < n && dif[j] >= 0){
                    if(prev <= dif[j]){
                        ans += dif[j] - prev;
                        prev = dif[j];
                    }
                    else prev = dif[j];
                    j++;
                }
                i = j - 1;
            }
            else{
                int k = i;
                while(k < n && dif[k] <= 0)dif[k] = abs(dif[k]), k++;
                ll j = i, prev = 0;
                while(j < k && dif[j] >= 0){
                    if(prev <= dif[j]){
                        ans += dif[j] - prev;
                        prev = dif[j];
                    }
                    else prev = dif[j];
                    j++;
                }
                i = j - 1;
            }
            // cout<<ans<<" ";
        }
        // cout<<endl;
        return ans;
    }
};
