class Solution:
    def compute_increments(self, array):
        if not array:
            return 0
        result = array[0]
        for i in range(1, len(array)):
            result += max(array[i] - array[i - 1], 0)
        return result

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        total_operations = 0
        size = len(nums)
        differences = [nums[i] - target[i] for i in range(size)]

        index = 0
        while index < size:
            temp_array = []

            if differences[index] < 0:
                while index < size and differences[index] <= 0:
                    temp_array.append(abs(differences[index]))
                    index += 1
                total_operations += self.compute_increments(temp_array)
            else:
                while index < size and differences[index] >= 0:
                    temp_array.append(differences[index])
                    index += 1
                total_operations += self.compute_increments(temp_array)

        return total_operations
