class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }
        long long res = 0;
        for (int i = 0; i < n; ++i) {
            if (i == 0) {
                res += abs(diff[i]);
            } else {
                if (1ll * diff[i - 1] * diff[i] > 0) {
                    if (abs(diff[i - 1]) < abs(diff[i])) {
                        res += abs(diff[i - 1] - diff[i]);
                    }
                } else {
                    res += abs(diff[i]);
                }
            }
        }
        return res;
    }
};
