class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        res = 0
        i = 0
        n = len(nums)
        arr = [t - x for t, x in zip(target, nums)]
        while i < n:
            if arr[i] == 0:
                i+=1
                continue
            j = i
            pos = (arr[j] > 0)
            val = inf if pos else -inf
            while j < n and arr[j] != 0 and ((arr[j] > 0) == pos):
                if pos:
                    val = min(val, arr[j])
                else:
                    val = max(val, arr[j])
                j+=1
            for k in range(i, j):
                arr[k]-=val
            res+=abs(val)

        return res
