class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> d(n+1);
        for(int i=0;i<n;i++)
            d[i]=target[i]-nums[i];
        for(int i=n;i>0;i--)
            d[i]-=d[i-1];
        long long ans=0;
        for(int i=0;i<=n;i++)
            ans+=abs(d[i]);
        return ans/2;
    }
};
