class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        #define ll long long
        int n=a.size();
        ll gt=0;
        ll sm=0;
        for(int i=0;i<n;++i){
            ll x=i==0?a[i]:a[i]-a[i-1];
            ll y=i==0?b[i]:b[i]-b[i-1];
            if(x>=y)
                gt+=x-y;
            else
                sm+=y-x;
        }
        return max(gt,sm);
    }
};
