class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        long long prev = 0;
        bool greater = true;
        for(int i=0;i<nums.size();i++) {
            // cout<<greater<<"\n";
            if(target[i] == nums[i]) {
                greater = true;
                prev = 0;
                continue;
            }
            else if(target[i] > nums[i]) {
                int absVal = abs(target[i] - nums[i]);
                if(greater) {
                    // int absVal = abs(target[i] - nums[i]);
                    if(absVal < prev) {
                        prev = absVal;
                        continue;
                    }
                    else {
                        absVal -= prev;
                        ans += absVal;
                        prev += absVal;
                    }
                }
                else {
                    ans += absVal;
                    prev = absVal;
                    greater = true;
                }
                // greater = true;
            }
            else {
                int absVal = abs(target[i] - nums[i]);
                if(!greater) {
                    // int absVal = abs(target[i] - nums[i]);
                    if(absVal < prev) {
                        prev = absVal;
                        continue;
                    }
                    else {
                        absVal -= prev;
                        ans += absVal;
                        prev += absVal;
                    }
                }
                else {
                    prev = absVal;
                    ans += absVal;
                    greater = false;
                }
                greater = false;
            }
        }
        return ans;
    }
};
