class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long res = 0;
        int cur = 0;
        for (int i=0; i<n; ++i) {
            if (target[i] > nums[i]) {
                cur = min(max(cur, 0), target[i] - nums[i]);
                res += target[i] - nums[i] - cur;
                cur = target[i] - nums[i];
            } else if (target[i] < nums[i]) {
                cur = max(min(cur, 0), target[i] - nums[i]);
                res += abs(target[i] - nums[i] - cur);
                cur = target[i] - nums[i];
            } else cur = 0;
        }
        return res;
    }
};
