class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums = [a - b for a, b in zip(nums, target)]
        offset = 0
        sign = 0
        ans = 0

        print(nums)
        def sign(v):
            return 1 if v > 0 else - 1

        for i in range(len(nums)):
            if i == 0 or sign(nums[i]) != sign(nums[i-1]):
                ans += abs(nums[i])
            elif sign(nums[i]) > 0 and nums[i] > nums[i-1]:
                ans += nums[i] - nums[i-1]
            elif sign(nums[i]) < 0 and nums[i] < nums[i-1]:
                ans += nums[i-1] - nums[i] 
        return ans
