func minimumOperations(nums []int, target []int) int64 {
	n := len(nums)
	for i := range target {
		target[i] -= nums[i]
	}
	d := make([]int, n)
	d[0] = target[0]
	for i := 1; i < n; i++ {
		d[i] = target[i] - target[i-1]
	}
    //fmt.Println(target,d)
	rst := 0
	prev, back := 0, n-1
	for prev < back {
		for prev < back && d[prev] <= 0 {
			prev++
		}
		for prev < back && d[back] >= 0 {
			back--
		}
		if prev == back {
			break
		}
		tmp := min(Abs(d[prev]), Abs(d[back]))
		d[prev] -= tmp
		d[back] += tmp
		rst += tmp
	}
	prev, back = 0, n-1
	for prev < back {
		for prev < back && d[prev] >= 0 {
			prev++
		}
		for prev < back && d[back] <= 0 {
			back--
		}
		if prev == back {
			break
		}
		tmp := min(Abs(d[prev]), Abs(d[back]))
		d[prev] += tmp
		d[back] -= tmp
		rst += tmp
	}
	for _, v := range d {
		rst += Abs(v)
	}
	return int64(rst)
}
func Abs(a int) int {
	if a >= 0 {
		return a
	}
	return -a
}
