class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n=nums.size(),prev=0,ans=0;
        for(long long i=0;i<n;i++) {
            long long t=target[i]-nums[i];
            if(t*prev<0) {
                ans+=abs(t);
            }
            else if(abs(t)>abs(prev)) {
                ans+=abs(t)-abs(prev);
            }
            prev=t;
        }
        return ans;
    }
};
