class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int len = nums.size();
        for(int i = 0; i < len; i++)
        {
            nums[i] -= target[i];
        }
        int cur = 0;
        for(int i = 0; i < len; i++)
        {
            if(cur > 0)
            {
                if(nums[i] > cur)
                {
                    ans += ((long long) (nums[i] - cur));
                }
                else if (nums[i] < 0)
                {
                    ans -= ((long long) nums[i]);
                }
            }
            else
            {
                if(nums[i] < cur)
                {
                    ans -= ((long long) (nums[i] - cur));
                }
                else if (nums[i] > 0)
                {
                    ans += ((long long) nums[i]);
                }
            }
            cur = nums[i];
        }
        return ans;
    }
};
