class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        var n = nums.length;
        long incr = 0, decr = 0;

        long ops = 0;
        for(var i=0; i<n; i++){
            var diff = target[i] - nums[i];
            if(diff > 0){
                if(incr < diff)
                    ops += (diff - incr);
                incr = diff;
                decr = 0;
            }
            else if(diff < 0){
                if(decr < -diff)
                    ops += ((-diff) - decr);
                decr = -diff;
                incr = 0;
            }
            else {
                incr = 0;
                decr = 0;
            }
        }

        return ops;
    }
}

