class Solution {
  public:
    long long minimumOperations(vector<int> &nums, vector<int> &target) {
        long long res = 0;
        int n = (int)nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; i++) {
            target[i] -= nums[i];
        }
        diff[0] = target[0];
        for (int i = 1; i < n; i++) {
            diff[i] = target[i] - target[i - 1];
        }
        res = 0;
        long long curr = diff[0];
        for (int i = 1; i < n; i++) {
            long long d = diff[i];
            if (curr * d < 0) {
                auto toadd = min(abs(curr), abs(d));
                res += toadd;
                curr += d;
            } else {
                curr += d;
            }
        }
        res += abs(curr);
        return res;
    }
};
