class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> p(n), t(n);
        for (int i = 0; i < n; i ++) {
            p[i] = nums[i] - target[i];
        }
        for (int i = 0; i < n; i ++) {
            t[i] = p[i] - (i > 0 ? p[i - 1] : 0);
        }
        long long ans = 0;
        long long w1 = 0, w2 = 0;
        for (int i = 0; i < n; i ++) {
            if (t[i] > 0) w1 += t[i];
            else w2 += -t[i];
        }
        ans += min(w1, w2);
        ans += abs(w1 - w2);
        return ans;
    }
};
