class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff = new int[n];
        for (int i = 0; i < n; i++){
            diff[i] = nums[i] - target[i];
        }
        long ops = Math.abs(diff[0]);
        int start = 0, index = 1;
        while (index < n){
            if ((diff[index] ^ diff[start]) >= 0){
                if (Math.abs(diff[index]) > Math.abs(diff[index-1])) ops += Math.abs(diff[index]) - Math.abs(diff[index-1]);
            }
            else{
                ops += Math.abs(diff[index]);
                start = index;
            }
            index++;
        }
        return ops;
    }
}
