class Solution {
public:
    long long func(vector<int>&dum){
        int n=dum.size();
        stack<int> st;
        long long ans=0;
        for(int i=0;i<n;i++){
            if(st.empty()){
                st.push(dum[i]);
            }
            else{
                if(st.top()<dum[i]){
                    st.push(dum[i]);
                }
                else{
                    ans+=st.top()-dum[i];
                    while(!st.empty() && st.top()>dum[i]){
                        st.pop();
                    }
                    st.push(dum[i]);
                }
            }
        }
        if(!st.empty()){
            ans+=st.top();
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;){
            if(target[i]>=nums[i]){
                vector<int> dum;
                while(i<n && target[i]>=nums[i]){
                    dum.push_back(target[i]-nums[i]);
                    i++;
                }
                ans+=func(dum);
            }
            else if(target[i]<=nums[i]){
                vector<int> dum;
                while(i<n && target[i]<=nums[i]){
                    dum.push_back(nums[i]-target[i]);
                    i++;
                }
                ans+=func(dum);
            }
            // else if(target[i]==nums[i]){
            //     i++;
            // }
        }
        return ans;
    }
};
