class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int prevDiff = nums[0] - target[0];
        ans = abs(prevDiff);
        for(int i =1; i < nums.size(); i++){
            int currDiff = nums[i] - target[i];
            if(prevDiff <= 0 && currDiff <= 0){
                if(currDiff <prevDiff){
                    ans += prevDiff - currDiff;
                }
            }
            else if(prevDiff > 0 && currDiff > 0){
                if(currDiff > prevDiff){
                    ans += currDiff - prevDiff;
                }
            }
            else{
                ans += abs(currDiff);
            }
            prevDiff = currDiff;

        }
        return ans;
    }
};
