def f(t):
    r = 0
    c = 0
    for k in t:
        if k > c:
            r += k - c
            c = k
        else:
            c = k
    return r

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        l = [t - k for k, t in zip(nums, target)]
        print(l)
        r = 0
        tmp = [0]
        flag = 0
        for k in l:
            if k == 0:
                r += f(tmp)
                tmp = [0]
                flag = 0
            elif k > 0:
                if flag != 1:
                    r += f(tmp)
                    tmp = [k]
                    flag = 1
                else:
                    tmp.append(k)
            else:
                if flag != -1:
                    r += f(tmp)
                    tmp = [-k]
                    flag = -1
                else:
                    tmp.append(-k)
        if tmp:
            r += f(tmp)
        return r
