class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int[] t = new int[nums.length];
        for (int i = 0; i < nums.length; i++) {
            t[i] = nums[i]-target[i];
        }
        long ans = Math.abs(t[0]);
        for (int i = 1; i < t.length; i++) {
            if(t[i]==0){
                continue;
            }
            if(t[i-1]==0){
                ans+=Math.abs(t[i]);
                continue;
            }
            if((t[i]>0&&t[i-1]>0) || (t[i]<0&&t[i-1]<0)){
                if(Math.abs(t[i])<Math.abs(t[i-1])){
                    continue;
                }
                ans+= Math.abs(Math.abs(t[i])-Math.abs(t[i-1]));
            }else {
                ans+=Math.abs(t[i]);
            }
        }
        return ans;
    }
}
