#define f first
#define s second
#define pb push_back
#define all(x) x.begin(),x.end()
#define sz(x) (long long)(x).size()
#define pii pair<long long,long long>

class Solution {
public:
    long long minimumOperations(vector<int> nums, vector<int> target) {
        //is long long
        long long n = sz(nums);
        long long lastop = nums[0]-target[0], carry=nums[0]-target[0], ans = abs(nums[0]-target[0]);
        for (long long i = 1; i < n; i++) {
            long long tmp = nums[i] - target[i];
            // debug(tmp);
            // debug(carry);
            // debug(ans);
            if(tmp==0){
                carry=0;
                lastop=0;
            }
            else if(tmp>0 && carry>=0){
                if(tmp>carry){
                    ans+=tmp-carry;
                    carry=tmp;
                    lastop=tmp;
                }
                else{
                    carry=tmp;
                    lastop=tmp;
                }
            }
            else if(tmp<0 && carry<=0){
                if(tmp<carry){
                    ans+=abs(carry-tmp);
                    carry=tmp;
                    lastop=tmp;
                }
                else{
                    carry=tmp;
                    lastop=tmp;
                }
            }
            else{
                carry=tmp;
                ans+=abs(tmp);
            }
        }
        return ans;
    }
};
