class Solution {
public:
    long long pos(vector<int>&v){
        long long count=  0;
        int ct = 0; 
        vector<int>buffer;
        bool lastPos = 1;
        int n = v.size();
        for(int i=0;i<n;i++){
            if(v[i]>=0){
                if(!lastPos){
                    count+=calculateOp(buffer);
                    buffer.clear();
                }
                buffer.push_back(abs(v[i]));
                lastPos = 1;
            }
            else{
                if(lastPos){
                    count+=calculateOp(buffer);
                    buffer.clear();
                }
                lastPos = 0;
                buffer.push_back(abs(v[i]));
            }
        }
        if(buffer.size()) count+=calculateOp(buffer);
        return count;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>d;
        for(int i=0;i<n;i++){
            d.push_back(nums[i]-target[i]);
        }  
        return pos(d);
    }
    long long calculateOp(vector<int>&v){ 
        int lm = -1;
        long long ct = 0;
        int n =v.size();
        for(int i=0;i<n;i++){
            if(v[i]<=lm){
                if(lm!=-1) lm = min(lm,v[i]);
                continue;
            }
            if(lm==-1){
                ct+=v[i];
                lm = v[i];
                continue;
            }
            ct+=(v[i]-lm); 
            lm = v[i];
        } 
        return ct;
    }
};
