class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        total = level = 0
        for delta in (n-t for n,t in zip(nums,target)):
            if delta > 0:
                if level < delta:
                    total += delta-max(0,level)
            else:
                if level > delta:
                    total += min(0,level)-delta
            level = delta
        return total
