class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long res = 0;
        int maxAdd = 0;
        int maxDis = 0;
        for (int i = 0; i < nums.length; i++) {
            int t1 = nums[i];
            int t2 = target[i];
            int dis = Math.abs(t1 - t2);
            if (t1 == t2){
                maxAdd = 0;
                maxDis = 0;
                continue;
            }
            if (t1 > t2){
                // 减
                if (dis <= maxDis){
                    maxDis = dis;
                }else{
                    res += (dis - maxDis);
                    maxDis = dis;
                }
                maxAdd = 0;
            }else{
                //加
                if (dis <= maxAdd){
                    maxAdd = dis;
                }else{
                    res += (dis - maxAdd);
                    maxAdd = dis;
                }
                maxDis = 0;
            }
        }
        return res;
    }
}
