using ll = long long;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        ll ans = 0;
        int n = a.size();
        for (int i = 0; i < n; i ++) {
            int x = b[i] - a[i];
            if (i) {
                int y = b[i - 1] - a[i - 1];
                if (x > 0) {
                    if (y >= x) {
                        x = 0;
                    } else {
                        x = min(x, x - y);
                    }
                } else {
                    if (y < x) {
                        x = 0;
                    } else if (0 >= y && y >= x) {
                        x = y - x;
                    } else {
                        x = -x;
                    }
                } 
            } else {
                x = abs(x);
            }
            ans += x;
        }
        return ans;
    }
};
