class Solution {
public:
    // Encounter number need to be decrecment, then forgive
    // diff = [3, 1, 6, 1, -5, -1, -3] ==> (3 + 0 + (6 - 1) + 0) + (5 + 0 + (3 - 1))
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0; i < n; i++)
            diff[i] = target[i] - nums[i];

        long long ans = 0;
        for(int i = 0; i < n;){
            int last = 0;
            while(i < n && diff[i] >= 0){
                ans += max(0, diff[i] - last);
                last = diff[i]; 
                i++;
            }

            last = 0;
            while(i < n && diff[i] <= 0){
                ans += max(0, -(diff[i] - last));
                last = diff[i]; 
                i++;
            }
        }
        return ans;
    }
};
