class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>distn(n + 10), distt(n + 10);
        typedef long long ll;
        for(int i = 1; i < n; i++){
            distn[i] = nums[i] - nums[i - 1];
            distt[i] = target[i] - target[i - 1];
        }
        distn[0] = nums[0]; distt[0] = target[0];
        ll a = 0, b = 0;
        for(int i = 0; i < n; i++) {
            if(distn[i] < distt[i]) a += distt[i] - distn[i];
            else if(distn[i] > distt[i]) b += distn[i] - distt[i];
        }

        ll ans = min(a, b) + abs(a - b);
        return ans;
    }
};
