#define ll long long
class Solution {
public:
    ll helper(vector<int> &x) {
        // all ele in x are positive
        // try to 0 x
        ll ans = x[0];
        for (int i = 1; i < x.size(); i++) {
            ans += max(x[i] - x[i-1], 0);
        }
        return ans;
    }
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diffs(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            diffs.push_back(nums[i] - target[i]);
        }
        ll ans = 0;
        for (int i = 0; i < diffs.size(); i++) {
            // while they are all the same sign, keep going
            if (!diffs[i]) continue;
            vector<int> same;
            int l = i;
            while (l < diffs.size() && diffs[l] && diffs[l] < 0 == diffs[i] < 0) {
                // find max abs
                same.push_back(abs(diffs[l]));
                l++;
            }
            ans += helper(same);
            i = l-1;
        }
        return ans;
    }
};
