class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long a = 0, b = 0,c=0;
        int n = nums.size();
        for(int i = 0; i < n; i++){
            if(nums[i] < target[i]){
                long long curr_diff = target[i] - nums[i];
                if(a < curr_diff){
                    c+= curr_diff - a;
                }
                a= curr_diff;
                b= 0;
            //   cout<<a<<" "<<b<<"\n";

            }
            else if(nums[i] > target[i]){
                long long curr_diff = nums[i] - target[i];
                if(b < curr_diff){
                    c+= curr_diff - b;
                }
                b= curr_diff;
                a= 0;
             //   cout<<a<<" "<<b<<"\n";
            }

            else{
                a = 0;
                b = 0;
            //   cout<<a<<" "<<b<<"\n";
            }
        }

        return c;
    }
};
