class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size(), i = 0, m = 0;
        long long s = 0;
        while(i < n){
            m = 0;
            while(i < n && target[i] == nums[i]){
                i++;
            }
            while(i < n && target[i] > nums[i]){
                if(target[i] - nums[i] > m){
                    s += (long long)(target[i] - nums[i] - m);
                    m = target[i] - nums[i];
                }
                else{
                    m = target[i] - nums[i];
                }
                i++;
            }
            m = 0;
            while(i < n && nums[i] > target[i]){
                if(nums[i] - target[i] > m){
                    s += (long long)(nums[i] - target[i] - m);
                    m = nums[i] - target[i];
                }
                else{
                    m = nums[i] - target[i];
                }
                i++;
            }
        }
        return s;
    }
};
