class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long operations = 0, n = nums.size(), prv = 0, cur;
        vector<int> dif;
        for(int i = 0; i < n; i++) {
            dif.push_back(target[i] - nums[i]);
        }

        for(int i = 0; i < n; i++) {
            if(prv <= 0) {
                if(dif[i] <= 0) {
                    if(dif[i] >= prv)
                        prv = dif[i];
                    else {
                        operations += prv - dif[i];
                        prv = dif[i];
                    }
                }
                else {
                    operations += dif[i];
                    prv = dif[i];
                }
            }
            else {
                if(dif[i] >= 0) {
                    if(dif[i] <= prv)
                        prv = dif[i];
                    else {
                        operations += dif[i] - prv;
                        prv = dif[i];
                    }
                }
                else {
                    operations += abs(dif[i]);
                    prv = dif[i];
                }
            }
        }
        return operations;
    }
};
