class Solution {
    long long process(vector<int> &v){
        long long temp=0;
        int bc=v.back();
        v.pop_back();
        while(!v.empty()){
            int bc2=v.back();
            v.pop_back();
            if(bc2<bc){
                temp+=bc-bc2;
            }
            bc=bc2;
        }
        temp+=bc;
        return temp;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       long long ans=0;
       int n=nums.size();
       vector<int> v;
       bool b=0;
       int num=nums[0]-target[0];
       if(target[0]>=nums[0]){
        b=1;
       }
       v.push_back(abs(num));
       for(int i=1;i<n;i++){
        int temp=target[i]-nums[i];
        if(temp==0){
            v.push_back(0);
        }
        else if(temp>0){
            if(b==0){
                ans+=process(v);
                b=1;
            }
            v.push_back(temp);
        }
        else {
            if(b){
                ans+=process(v);
                b=0;
            }
            v.push_back(abs(temp));
        }
       }
       ans+=process(v);
       return ans; 
    }
};
