class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0;i < n;i++)
        {
            diff[i] = nums[i] - target[i];
        }
        int ind = 0;
        long long inc = 0,dec = 0;
        long long ans = 0;
        while(ind < n)
        {
            if(diff[ind] >= 0)
            {
                inc = diff[ind];
                ind++;
                while(ind < n && diff[ind] >= 0)
                {
                    inc += max(0,diff[ind]-diff[ind-1]);
                    ind++;
                }
                ans += inc;
                inc = 0;
            }else
            {
                dec = abs(diff[ind]);
                ind++;
                while(ind < n && diff[ind] < 0)
                {
                    dec += max(0,diff[ind-1]-diff[ind]);
                    ind++;
                }
                ans += dec;
                dec = 0;
            }
        }
        return ans;
    }
};
