class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff(nums.size());
        for(int i = 0; i<nums.size(); i++){
            diff[i] = target[i] - nums[i];
        }

        int opr = 0, prev = 0;
        long long ans = 0;
        for(int i = 0; i<nums.size(); i++){
            if(diff[i] == 0){
                prev = 0;
                opr = 0;
            }else if(diff[i] > 0){
                if(prev == 1){
                    int need = diff[i];
                    if(need == opr) continue;
                    else if(need < opr){
                        opr = need;
                    }else{
                        ans += (need - opr);
                        opr = need;
                    }
                }else{
                    int need = diff[i];
                    opr = need;
                    ans += opr;
                    prev = 1;
                }
            }else{
                if(prev == 0){
                    int need = abs(diff[i]);
                    if(opr == need) continue;
                    else if(opr > need) opr = need;
                    else{
                        ans += (need - opr);
                        opr = need;
                    }
                }else{
                    prev = 0;
                    int need = abs(diff[i]);
                    ans += need;
                    opr = need;
                }
            }
        }
        return ans;
    }
};
