class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;++i)diff[i]=target[i]-nums[i];
        long long res=0;
        res+=abs(diff[0]);
        for(int i=1;i<n;++i){
            if(diff[i]>=0){
                if(diff[i-1]>=0){
                    if(diff[i-1]>=diff[i]){}
                    else res+=diff[i]-diff[i-1];
                }else{
                    res+=abs(diff[i]);
                }
            }else{
                if(diff[i-1]<0){
                    if(diff[i-1]<=diff[i]){}
                    else res+=diff[i-1]-diff[i];
                }else{
                    res+=abs(diff[i]);
                }
            }
        }
        return res;
    }
};
